package Java.Small.Program;

public class StringPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("String pattern is:");
		String s = "";
		int n=5;
		for (int i = 1; i <= n; ++i) {
		    s += "* ";
		    System.out.println(s);
		}
		

	}

}
